import axios from 'axios'
import React, { useContext, useEffect, useState } from 'react'
import DefaultContext from '../contexts/DefaultContext'
import { useParams } from 'react-router-dom'
import { Input } from 'reactstrap'
import alertify from 'alertifyjs'

const EmployeeManagement = () => {
    const { token } = useContext(DefaultContext)
    const [users, setUsers] = useState([])
    const [employees, setEmployees] = useState([])
    const [selectedUser, setSelectedUser] = useState()
    const { id } = useParams()

    const getData = async () => {
        try {
            const { data } = await axios({
                method: "Get",
                baseURL: 'https://localhost:7195/api',
                url: `/users/UsersForEmployeeManagement/${id}`,
                headers: {
                    "Authorization": `Bearer ${token}`
                }
            })
            setUsers(data.unEmployedUsers)
            setEmployees(data.employees)
            console.log(data)
            console.log("--------------")

        }
        catch (error) {
            console.log(error)
        }

        console.log(users)
        console.log(employees)
    }

    const removeEmployee = employeeId => {

    }

    const changeSelectedUser = event => {
        let value = event.target.value

        let selected = users.find(p=>p.id == value)

        setSelectedUser(selected)
    }

    const addEmployee = () => {
        axios({
                method: "Post",
                baseURL: 'https://localhost:7195/api',
                url: "/hospitals/addEmployee",
                headers: {
                    "Authorization": `Bearer ${token}`
                },
                data:selectedUser                
            })
            .then((data) =>{
                data ? alertify.success("Personel Eklendi.") : alertify.error("Personel Eklenemedi.")
            })
            .catch(()=>{
                alertify.error("Personel Eklenemedi.")
            })
    }

    useEffect(() => {
        getData()
    }, [])

    return (
        <div>
            <h3>Personel Ekle</h3>
            <div class="input-group input-group-lg mb-3">
                <Input type='select' id="users" onChange={changeSelectedUser}>
                    <option value="0" selected hidden>Se√ßiniz</option>
                    {
                         users && users.length > 0 && users.map((user, index) => (
                            <option value={user.id} >{user.fullName}</option>
                         ))
                    }
                </Input>
                <div class="btn-group">
                    <button type="button" class="btn btn-outline-success" onclick="addEmployee()">Personel Ekle</button>
                </div>
            </div>

            <ul id="employeeList">
                {
                    employees && employees.length > 0 && employees.map((employee, index) => (
                        <li key={index} class="list-group-item fs-5 d-flex justify-content-between align-items-center">
                            <button class="close btn badge text-danger float-end me-2" onClick={()=>removeEmployee(employee.id)}>X</button>
                            <div class="me-auto">
                                {employee.fullName}
                            </div>
                        </li>
                    ))
                }
            </ul>
        </div>
    )
}

export default EmployeeManagement
