import { useFormik } from 'formik'
import React, { useContext } from 'react'
import { NavLink, useNavigate } from 'react-router-dom'
import { Button, Form, FormGroup, Input } from 'reactstrap'
import validationSchema from "./validations"
import axios from 'axios'
import DefaultContext from '../../contexts/DefaultContext'

const LoginForm = () => {
    const { setToken, setExpire, setBloodGroup, setCity, setUserRole } = useContext(DefaultContext)
    const navigate = useNavigate()
    const { handleSubmit, handleChange, handleBlur, values, errors, touched, isSubmitting } = useFormik({
        initialValues: {
            userName: "",
            password: "",
            isKeepLoggedIn: true
        },
        onSubmit: (values, bag) => {
            let a = { ...values, isKeepLoggedIn: true }
            axios({
                baseURL: 'https://localhost:7195/api',
                url: '/auth/login',
                method: 'post',
                data: a
            })
                .then(response => {
                    setToken(response.data.token)
                    setExpire(response.data.expire)
                    setBloodGroup(response.data.bloodGroup)
                    setCity(response.data.city)
                    setUserRole(response.data.role)
                    navigate("/home")
                })
                .catch(errors => {
                    console.log(errors)
                })

            console.log(values)
            bag.resetForm()
        },
        validationSchema
    })

    return (
        <div>
            <Form onSubmit={handleSubmit}>
                <FormGroup>
                    <Input
                        id="userName"
                        name="userName"
                        placeholder="Kullanıcı Adı"
                        value={values.userName}
                        onBlur={handleBlur("userName")}
                        onChange={handleChange}
                    ></Input>
                    {errors.userName && touched.userName && <div className="text-danger">{errors.userName}</div>}
                </FormGroup>
                <FormGroup>
                    <Input
                        id="password"
                        name="password"
                        type="password"
                        placeholder="Şifre"
                        value={values.password}
                        onBlur={handleBlur("password")}
                        onChange={handleChange}
                    ></Input>
                    {errors.password && touched.password && <div className="text-danger">{errors.password}</div>}
                </FormGroup>
                <Button type="submit" color="success" disabled={isSubmitting}>
                    Giriş Yap
                </Button>
            </Form>
            <div className="links">
                <strong className="text-dark-emphasis">Hesabınız yok mu?</strong>
                <NavLink
                    to="/register"
                >
                    <strong>Kayıt Ol</strong>
                </NavLink>
            </div>
        </div>
    )
}

export default LoginForm
